mapper.update:
update farms.farm_inventory_item_attributes
set inventory_item_code = (
        select iic.inventory_item_code
        from farms.farm_inventory_item_codes iic
        where iic.inventory_item_code = #{dto.inventoryItemCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
    ),
    rollup_inventory_item_code = (
        select iic1.inventory_item_code
        from farms.farm_inventory_item_codes iic1
        where iic1.inventory_item_code = #{dto.rollupInventoryItemCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
    ),
    revision_count = revision_count + 1,
    who_updated = #{userId, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN},
    when_updated = now()
where inventory_item_attrib_id = #{dto.inventoryItemAttributeId, javaType=java.lang.Long, jdbcType=BIGINT, mode=IN}
