mapper.update:
update farms.farm_structure_group_attributs
set structure_group_code = (
        select sgc.structure_group_code
        from farms.farm_structure_group_codes sgc
        where sgc.structure_group_code = #{dto.structureGroupCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
    ),
    rollup_structure_group_code = (
        select sgc1.structure_group_code
        from farms.farm_structure_group_codes sgc1
        where sgc1.structure_group_code = #{dto.rollupStructureGroupCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
    ),
    revision_count = revision_count + 1,
    who_updated = #{userId, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN},
    when_updated = now()
where structure_group_attrib_id = #{dto.structureGroupAttributeId, javaType=java.lang.Long, jdbcType=BIGINT, mode=IN}
